mapper.select:
select s.*, c.name clazzName from student s left join clazz c on s.clazz_id = c.id


        s.name like concat('%', #{name}, '%')


        and s.degree = #{degree}


        and s.clazz_id = #{clazzId}


order by c.update_time desc

mapper.update:
update student
name = #{name}, no = #{no}, phone = #{phone}, gender = #{gender}, degree = #{degree}, id_card = #{idCard}, is_college = #{isCollege}, address = #{address}, graduation_date = #{graduationDate}, violation_count = #{violationCount}, violation_score = #{violationScore}, update_time = #{updateTime},
where id = #{id}
